mapper.select:
SELECT COUNT(*) FROM board


        AND title LIKE CONCAT('%', #{title}, '%')


        AND categorykey IN (
            SELECT `key` 
            FROM key_table 
            WHERE value = #{categoryName} 
            AND `table` = 'board'
            AND `column` = 'categorykey'
        )

    AND isdeleted = 0

        AND create_dtm BETWEEN #{create_start_date} AND #{create_end_date}


        AND create_dtm >= #{create_start_date}


        AND create_dtm <= #{create_end_date}

SELECT * FROM (
    SELECT @RN:=@RN+1 AS rnum, a.* FROM (
        SELECT b.*, k.value AS categoryValue 
        FROM board b
        LEFT JOIN key_table k ON b.categorykey = k.key
            AND k.`table` = 'board'
            AND k.`column` = 'categorykey'


                b.title LIKE CONCAT('%', #{title}, '%')


                AND categorykey IN (
                    SELECT `key` 
                    FROM key_table 
                    WHERE value = #{categoryName} 
                    AND `table` = 'board'
                    AND `column` = 'categorykey'
                )

            AND b.isdeleted = 0

                AND create_dtm BETWEEN #{create_start_date} AND #{create_end_date}


                AND create_dtm >= #{create_start_date}


                AND create_dtm <= #{create_end_date}


        ORDER BY b.boardkey DESC
    ) a, (SELECT @RN:=0) b
) c 
WHERE c.rnum BETWEEN #{begin} AND #{end}

mapper.update:
		UPDATE board
    SET userkey = #{userkey}, townkey = #{townkey}, categorykey = #{categorykey}, title = #{title}, content = #{content}, viewqty = 0, likeqty = 0, update_dtm = NOW(), isdeleted = 0
    WHERE boardkey = #{boardkey}

mapper.delete:
DELETE b FROM board b
JOIN ( SELECT boardkey FROM board WHERE userkey = #{userkey} ORDER BY create_dtm DESC LIMIT 1)
AS subquery ON b.boardkey = subquery.boardkey;

mapper.update:
UPDATE board
SET title = #{title}, content = #{content}, update_dtm = NOW()
WHERE boardkey = #{boardkey}

Update board
SET isdeleted = 1, delete_dtm = NOW()
WHERE boardkey = #{boardkey}

UPDATE board
SET viewqty = viewqty + 1
WHERE boardkey = #{boardkey}
